import React from "react";

import  { ClockStyle }  from "./clockStyle";

function checkTime(i) {
  if (i < 10) {
    i = "0" + i;
  }
  return i;
}

export default class Clock extends React.Component {
  constructor(...args) {
    super(...args);
    this.state = {
      date: ``
    };
  }

  startTime() {
    const today = new Date();
    const h = today.getHours();
    const m = checkTime(today.getMinutes());
    const s = checkTime(today.getSeconds());
    const dd = checkTime(today.getDay());
    const mm = checkTime(today.getMonth());

    this.setState({ date: h + ":" + m + ":" + s + ", DE " + dd + " "+ mm});
    this.timeout = setTimeout(() => this.startTime(), 500);
  }

  componentDidMount() {
    this.startTime();
  }
  componentWillUnmount() {
    if (!this.timeout) return;
    clearTimeout(this.timeout);
  }
  render() {
    return <ClockStyle>Bem vindo, s√£o {this.state.date}</ClockStyle>;
  }
}
