import styled from 'styled-components';

export const Container = styled.div`
    background: #232329;
    border-radius: 10px;
    border: 2px solid #232129;
    padding: 16px;
    width: 100%;
    color: #666300;
    font-family: 'Nunito Sans', sans-serif;

    display: flex;
    align-items: center;

    & + div {
        margin-top: 8px;
    }

    input {
        flex: 1;
        background: transparent;
        border: 0;
        color: #f4ede8;

        &::placeholder {
            color: #666300;
        }
    }

    svg {
        margin-right: 16px;
    }
`;
